import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { LoginRequest } from 'src/app/models/usuario';
import { UsuarioService } from 'src/app/services/usuario.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  loginRequest: LoginRequest = {
    usuario: '',
    pass: ''
  };

  constructor(private usuarioService: UsuarioService,
    private router: Router
  ) {}


  ngOnInit(){

  }

  onSubmit(): void {
    this.usuarioService.login(this.loginRequest).subscribe(response => {
      if (response) {
        this.router.navigate(['/home']);
      } else {
        console.log('Credenciales incorrectas');
      }
    });
  }

  goToRegisterForm() {
    this.router.navigate(['/Register']);
  }

}
<div ng-app="App" class="page-container">
  <div class="form-container" ng-class="done">
    <div class="login-form">
      <h3>Enter the awesomeness!</h3>
      <div>
        <input type="text" placeholder="User">
      </div>

      <div>
        <input type="password" placeholder="Password">
      </div>

      <button data-loading-btn class="">
        <span data-loaded-msg="Thank you!">Log in</span>
      </button>
    </div>
    <div class="thank-msg">
      <h3>You are logged in!</h3>
      <button ng-click="done = ''">Try Again</button>
    </div>
    <div class="credits" (click)="goToRegisterForm()">
      <a >Registrarce</a>
    </div>
  </div>
</div>
