import { Component, OnInit } from '@angular/core';
import { LoginRequest } from 'src/app/models/usuario';
import { UsuarioService } from 'src/app/services/usuario.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  loginRequest: LoginRequest = {
    usuario: '',
    pass: ''
  };

  constructor(private usuarioService: UsuarioService) {}


  ngOnInit(){

  }

  onSubmit(): void {
    this.usuarioService.login(this.loginRequest).subscribe(response => {
      if (response) {
        console.log('Login exitoso:', response);
      } else {
        console.log('Credenciales incorrectas');
      }
    });
  }

}
<h2>Login</h2>
<form (ngSubmit)="onSubmit()">
  <label for="usuario">Usuario</label>
  <input id="usuario" [(ngModel)]="loginRequest.usuario" name="usuario" required>

  <label for="pass">Contraseña</label>
  <input id="pass" [(ngModel)]="loginRequest.pass" name="pass" type="password" required>

  <button type="submit">Login</button>
</form>


<div class="login-wrap">
  <h2>Login</h2>

  <div class="form">
    <input type="text" placeholder="Username" [(ngModel)]="loginRequest.usuario" name="un" />
    <input type="password" placeholder="Password" [(ngModel)]="loginRequest.pass" name="pw" />
    <button> Sign in </button>
    <a href="#"> <p> Don't have an account? Register </p></a>
  </div>
</div>
